sysevent_in_email_action.script:
(function runAction(/*GlideRecord*/ current, /*GlideRecord*/ event, /*EmailWrapper*/ email, /*ScopedEmailLogger*/ logger, /*EmailClassifier*/ classifier) {

	// Implement email action here
	//Add the sender with some static text to the Short description field,
	//then update the record in the database
	current.short_description = "Investigate reply to birthday email from " + email.sender;
	current.update();

})(current, event, email, logger, classifier);
